INSERT INTO `mtr_rueckkopplung_datenmaske` (`id`, `gruppe_id`, `teilnehmer_id`, `fach`, `datum`, `lehrkraft`, `thema`, `bemerkung`, `erstellt_am`, `metr_kognition`, `metr_sozial`, `metr_affektiv`, `metr_metakog`, `metr_kohaerenz`) VALUES
(1, 2, 20, 'PHY', '2025-09-29', 'Gellert, Manuela', NULL, '', '2025-11-10 04:55:44', NULL, NULL, NULL, NULL, NULL),
(2, 2, 20, 'PHY', '2025-11-03', 'Thiele, Dipl.-Ing. Olaf', 'Prüfungsvorbereitung: BLF', 'stellt manchmal Fragen, die ein tieferes Verständnis für das aktuelle Thema zeigen | hat zuletzt deutlich von der individuellen Unterstützung in der Nachhilfe profitiert | setzt Prioritäten bei Lernaufgaben sehr sinnvoll | braucht selten Unterstützung bei der Anwendung neuer Lernmethoden | gibt nur manchmal zu erkennen, wenn er ein Problem mit einer Aufgabenstellung hat', '2025-11-10 04:55:44', NULL, NULL, NULL, NULL, NULL),
(3, 2, 20, 'MAT', '2025-10-27', 'Thiele, Dipl.-Ing. Olaf', 'Weitere Themen: Stochastik Üb 2', 'konnte Wissen in der heutigen Stunde weitgehend festigen | Selbstständigkeit hat sich in den letzten Wochen deutlich verbessert | kann sich sehr gut auf eine Aufgabe konzentrieren, auch wenn diese als uninteressant empfunden wird | braucht selten Unterstützung bei der Anwendung neuer Lernmethoden | vermeidet die Wiederholung von zuvor gemachten Fehlern erfolgreich', '2025-11-10 04:55:44', NULL, NULL, NULL, NULL, NULL),
(4, 7, 20, 'PHY', '2025-10-24', 'Thiele, Dipl.-Ing. Olaf', 'Prüfungsvorbereitung: BLF', 'fühlt sich ziemlich sicher bei der Anwendung des Gelernten | Einstellung zum Lernen hat sich in letzter Zeit stark verbessert | ist sehr offen für andere Lösungsansätze | kann die Lernzeit für mehrere Tests normalerweise effektiv einteilen | kann umfangreiches Wissen in der Regel sinnvoll zusammenfassen | nM Teil B Staudamm', '2025-11-10 04:55:44', NULL, NULL, NULL, NULL, NULL),
(5, 2, 20, 'PHY', '2025-10-20', 'Thiele, Dipl.-Ing. Olaf', 'Weitere Themen: quadrErg', 'ist überwiegend in der Lage, die Fehler einer fremden Lösung zu begründen und zu berichtigen | Einstellung zum Lernen hat sich in letzter Zeit verbessert | gibt oft zu erkennen, wenn er etwas nicht verstanden hat | schafft normalerweise alle Aufgaben in einer Klassenarbeit oder Leistungskontrollen in der dafür angesetzten Zeit | erreicht in der Regel vereinbarte Lernziele | Wachstum/Zerfall', '2025-11-10 04:55:44', NULL, NULL, NULL, NULL, NULL),
(6, 2, 20, 'PHY', '2025-09-29', 'Thiele, Dipl.-Ing. Olaf', 'Weitere Themen: BLF', 'hat sich in Bereichen, die zuvor als schwierig identifiziert wurden, erstaunlich verbessert | hat in letzter Zeit ein deutlich besseres Verständnis für die Anforderungen der Lehrkraft in der Schule entwickelt | ergreift oft die Möglichkeit, Hilfsmittel (z.B. Taschenrechner, Tafelwerk, Wörterbuch) zu nutzen | benötigt fast keine Zeit, um nach einer Ablenkung die Arbeit wieder aufzunehmen | kommuniziert seine Gedanken und Ideen teilweise verständlich und nachvollziehbar', '2025-11-10 04:55:44', NULL, NULL, NULL, NULL, NULL),
(7, 2, 20, 'MAT', '2025-09-29', 'Gellert, Manuela', NULL, '', '2025-11-10 04:55:44', NULL, NULL, NULL, NULL, NULL),
(8, 7, 20, 'PHY', '2025-09-26', 'Thiele, Dipl.-Ing. Olaf', 'Prüfungsvorbereitung: BLF', 'beherrscht die spezifischen Fachbegriffe zuverlässig | Vertrauen in die eigenen Fähigkeiten hat sich in den letzten Wochen stark verbessert | wird von schwierigeren Aufgaben immerzu motiviert | arbeitet überwiegend in einem angemessenen Tempo | erreicht in der Regel vereinbarte Lernziele', '2025-11-10 04:55:44', NULL, NULL, NULL, NULL, NULL),
(9, 2, 20, 'MAT', '2025-09-22', 'Thiele, Dipl.-Ing. Olaf', 'Weitere Themen: trigFkt', 'nutzt oft die vermittelten Methoden | Vertrauen in die eigenen Fähigkeiten hat sich in den letzten Wochen verbessert | kann den eigenen Lösungsweg häufig nachvollziehbar schriftlich präsentieren | bittet aktiv um Feedback | kann umfangreiches Wissen in der Regel sinnvoll zusammenfassen', '2025-11-10 04:55:44', NULL, NULL, NULL, NULL, NULL),
(10, 7, 20, 'PHY', '2025-09-19', 'Thiele, Dipl.-Ing. Olaf', 'Trigonometrie: Üb', 'beherrscht die spezifischen Fachbegriffe zuverlässig | Notenentwicklung in den letzten Wochen unverändert | kann Wissen, das in den letzten Nachhilfestunden thematisiert wurde, meistens abrufen | hält Fristen für Hausaufgaben und Projekte sehr konsequent ein | reagiert interessiert auf neue Herausforderungen', '2025-11-10 04:55:44', NULL, NULL, NULL, NULL, NULL),
(11, 2, 20, 'MAT', '2025-09-15', 'Thiele, Dipl.-Ing. Olaf', 'Weitere Themen: VorbBLF', 'erinnert sich gut an den zuvor behandelten Stoff | Fähigkeit zum Verstehen und Analysieren komplexer Sachverhalte hat sich über die letzten Wochen verbessert | kann konstruktives Feedback gut umsetzen, um die eigene Leistung zu verbessern | zeigt sich zielstrebig beim Erreichen von Lernzielen | nutzt oft abwechslungsreiche Formulierungen', '2025-11-10 04:55:44', NULL, NULL, NULL, NULL, NULL),
(12, 7, 20, 'PHY', '2025-09-12', 'Thiele, Dipl.-Ing. Olaf', 'Quadratische Funktion/Gleichung: BLF T B', 'muss ab und zu an grundlegende Vorgehensweisen des Faches MAT erinnert werden | wendet die in der Nachhilfe gelernten Strategien meistens auch außerhalb der Nachhilfestunden an | ist offen gegenüber Kritik | formuliert Fragen weniger verständlich | benötigt wenig Zeit, um nach einer Ablenkung die Arbeit wieder aufzunehmen', '2025-11-10 04:55:44', NULL, NULL, NULL, NULL, NULL),
(13, 2, 20, 'MAT', '2025-09-08', 'Thiele, Dipl.-Ing. Olaf', 'Newtonsche Gesetze: VorbLK', 'kann erlerntes Wissen auch bei unangekündigten Tests zuverlässig abrufen | Geschwindigkeit beim Lösen von Aufgaben hat sich in den letzten Stunden ohne Qualitätsverlust erhöht | kann den eigenen Lösungsweg häufig nachvollziehbar mündlich präsentieren | versucht sich oftmals erneut an Aufgaben, mit denen es kurz zuvor Probleme gab | ist oft fähig, Aufgaben ohne direkte Anleitung zu beginnen', '2025-11-10 04:55:44', NULL, NULL, NULL, NULL, NULL),
(14, 7, 20, 'PHY', '2025-09-05', 'Thiele, Dipl.-Ing. Olaf', 'Weitere Themen: vorbblf', 'beherrscht die spezifischen Fachbegriffe zuverlässig | Einstellung zum Fach MAT hat sich in den letzten Stunden stark verbessert | arbeitet konsequent auf langfristige Ziele hin, bei denen sofortige Erfolge gelegentlich ausbleiben | kann sich zuverlässig an früher Gelerntes erinnern und es nutzen | braucht selten Unterstützung bei der Anwendung neuer Lernmethoden', '2025-11-10 04:55:44', NULL, NULL, NULL, NULL, NULL),
(15, 2, 20, 'PHY', '2025-09-01', 'Thiele, Dipl.-Ing. Olaf', 'Weitere Themen: quGl/quErg', 'beherrscht die spezifischen Fachbegriffe zuverlässig | hat zuletzt stark von der individuellen Unterstützung in der Nachhilfe profitiert | kann gut Zusammenhänge zwischen verschiedenen Informationen erkennen | schlägt gelegentlich sinnvolle Übungen oder Themen vor, an denen er arbeiten möchte | ist sehr offen für neue Lehrmethoden oder -ansätze', '2025-11-10 04:55:44', NULL, NULL, NULL, NULL, NULL),
